package com.jaxio.demo.web.domain.support;

import java.util.Map;

import org.primefaces.model.LazyDataModel;
import org.primefaces.model.SortOrder;

import com.jaxio.demo.dao.support.OrderBy;
import com.jaxio.demo.dao.support.OrderByDirection;
import com.jaxio.demo.dao.support.SearchParameters;

/**
 * Base search form for JPA entities. It supports 'Server Side Pagination' using PrimeFaces LazyDataModel.
 */
public abstract class GenericLazyDataModel<E> extends LazyDataModel<E> {
    private static final long serialVersionUID = 1L;

    private E selectedRow;
    private boolean bypassFirstOffset = true;

    /**
     * _HACK_
     * Call it from your flow when a 'search' event is received to bypass offset sent by primefaces paginator. 
     */
    public void onSearch() {
        bypassFirstOffset = true;
    }

    /**
     * Applies the passed parameters to the passed SearchParameters.
     * Note: filters are not supported for the moment
     * @return the passed searchParameters
     */
    protected SearchParameters populateSearchParameters(SearchParameters sp, int first, int pageSize, String sortField, SortOrder sortOrder,
            Map<String, String> filters) {
        sp.setFirstResult(bypassFirstOffset ? 0 : first);
        bypassFirstOffset = false;
        sp.setMaxResults(pageSize);

        sp.clearOrders();
        if (sortField != null) {
            OrderByDirection obd = convert(sortOrder);
            sp.addOrderBy(new OrderBy(sortField, obd));
        }
        return sp;
    }

    /**
     * Returns the currently selected row. To be called from your flow upon a "selectXxx" transition.
     */
    public E getSelectedRow() {
        return selectedRow;
    }

    /**
     * Set the currently selected row. To be called from your dataTable.
     */
    public void setSelectedRow(E selectedRow) {
        this.selectedRow = selectedRow;
    }

    /**
     * Convert PrimeFaces SortOrder to our OrderByDirection.
     */
    private static OrderByDirection convert(SortOrder ob) {
        switch (ob) {
        case DESCENDING:
            return OrderByDirection.DESC;
        default:
            return OrderByDirection.ASC;
        }
    }
}