package com.jaxio.demo.formatter;

import static org.apache.commons.lang.StringUtils.isBlank;

import java.text.ParseException;
import java.util.Locale;

import com.jaxio.demo.domain.Document;
import com.jaxio.demo.domain.Document_;
import com.jaxio.demo.repository.DocumentRepository;
import com.jaxio.demo.formatter.support.DiscoverableFormatter;
import com.jaxio.demo.formatter.support.FormatterUtil;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Component;

/**
 * {@link Formatter} for {@link Document} that is picked by automatically by {@link CustomFormattingConversionServiceFactory}
 * 
 * @see Formatter
 * @see CustomFormattingConversionServiceFactory
 */
@Component
public class DocumentFormatter implements DiscoverableFormatter<Document> {

    @Autowired
    private DocumentRepository documentRepository;

    @Override
    public Class<Document> getTarget() {
        return Document.class;
    }

    @Override
    public String print(Document document, Locale locale) {
        return FormatterUtil.print(Document_.documentContentType.getName() + "+'/'+"
                + Document_.documentFileName.getName(), document, locale);
    }

    @Override
    public Document parse(String text, Locale locale) throws ParseException {
        if (isBlank(text)) {
            return new Document();
        }
        Document document = documentRepository.findOne(text);
        return document != null ? document : new Document();
    }
}
