package com.jaxio.demo.web.controller;

import static org.springframework.http.HttpStatus.INTERNAL_SERVER_ERROR;
import static org.springframework.web.bind.annotation.RequestMethod.DELETE;
import static org.springframework.web.bind.annotation.RequestMethod.GET;
import static org.springframework.web.bind.annotation.RequestMethod.POST;
import static org.springframework.web.bind.annotation.RequestMethod.PUT;
import static com.google.common.collect.Lists.newArrayList;

import java.io.PrintWriter;
import java.util.List;

import javax.validation.Valid;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.data.domain.Page;
import org.springframework.format.support.FormattingConversionService;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.ExceptionHandler;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.ResponseBody;
import org.springframework.web.bind.annotation.ResponseStatus;

import com.jaxio.demo.web.util.AutoCompleteResult;
import com.jaxio.demo.web.util.SearchParameters;
import com.jaxio.demo.domain.Book;
import com.jaxio.demo.repository.BookRepository;

@Controller
@RequestMapping("domain/rest/book/")
public class BookRestController {
    @Autowired
    public FormattingConversionService formattingConversionService;

    @Autowired
    private BookRepository bookRepository;

    @RequestMapping(value = "/", method = POST)
    @ResponseBody
    public Book create(@Valid Book book) {
        bookRepository.save(book);
        return book.copy();
    }

    @RequestMapping(value = "/", method = POST, headers = "content-type:application/json")
    @ResponseBody
    public Book jsonCreate(@Valid @RequestBody Book book) {
        bookRepository.save(book);
        return book.copy();
    }

    @RequestMapping(value = "/", method = GET)
    @ResponseBody
    public List<Book> list(Book book, SearchParameters searchParameters) {
        Page<Book> page = bookRepository.findWithExample(book, searchParameters.toPageRequest());
        List<Book> ret = newArrayList();
        for (Book _book : page.getContent()) {
            ret.add(_book.copy());
        }
        return ret;
    }

    @RequestMapping(value = "/{pk}", method = GET)
    @ResponseBody
    public Book get(@PathVariable("pk") Book book) {
        return book.copy();
    }

    @RequestMapping(value = "/{pk}", method = PUT)
    @ResponseBody
    public Book update(@PathVariable("pk") Book bookToUpdate, @Valid Book book) {
        book.copyTo(bookToUpdate);
        bookRepository.save(bookToUpdate);
        return bookToUpdate.copy();
    }

    @RequestMapping(value = "/{pk}", method = DELETE)
    @ResponseBody
    public boolean delete(@PathVariable("pk") Book book) {
        bookRepository.delete(book);
        return true;
    }

    @RequestMapping("/autocomplete")
    @ResponseBody
    public List<AutoCompleteResult> autocomplete(@RequestParam(value = "term", required = false) String searchPattern,
            SearchParameters search) {
        if (searchPattern != null && !searchPattern.isEmpty()) {
            search.setSearchPattern(searchPattern);
        }

        List<AutoCompleteResult> ret = newArrayList();
        for (Book book : bookRepository.find(search.getSearchPattern())) {
            String objectPk = book.getId().toString();
            String objectLabel = formattingConversionService.convert(book, String.class);
            ret.add(new AutoCompleteResult(objectPk, objectLabel));
        }
        return ret;
    }

    @ExceptionHandler()
    @ResponseStatus(value = INTERNAL_SERVER_ERROR)
    public void exception(Exception e, PrintWriter out) {
        out.write(e.getMessage());
    }
}