package com.jaxio.demo.repository;

import static org.fest.assertions.Assertions.assertThat;

import javax.persistence.EntityManager;
import javax.persistence.PersistenceContext;

import org.apache.log4j.Logger;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.test.context.ContextConfiguration;
import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;
import org.springframework.transaction.annotation.Transactional;

import com.google.common.collect.Sets;

import com.jaxio.demo.domain.Book;
import com.jaxio.demo.repository.BookGenerator;
import com.jaxio.demo.repository.BookRepository;

/**
 * Integration test on {@link BookRepository}
 */
@RunWith(SpringJUnit4ClassRunner.class)
@ContextConfiguration(locations = { "classpath*:spring/applicationContext-test.xml" })
@Transactional
public class BookRepositoryWithRealSessionTest {
    @SuppressWarnings("unused")
    private static final Logger log = Logger.getLogger(BookRepositoryWithRealSessionTest.class);

    @PersistenceContext
    private EntityManager entityManager;

    @Autowired
    private BookRepository bookRepository;

    @Autowired
    private BookGenerator bookGenerator;

    @Test
    public void identityShouldBePreserved() {
        Book book = bookGenerator.getBook();

        // add it to a Set before saving (force equals/hashcode)
        Sets.newHashSet(book);

        bookRepository.save(book);
        entityManager.flush();

        // reload it from cache and check equality
        Integer id = book.getId();
        assertThat(book).isEqualTo(bookRepository.findOne(id));

        // clear cache to force reload from db
        entityManager.clear();

        // pk are equals...
        assertThat(book.getId()).isEqualTo(bookRepository.findOne(id).getId());

        // but since you do not use a business key, equality is lost.
        assertThat(book).isNotEqualTo(bookRepository.findOne(id));
    }
}