package fr.bdf.printer.more;

import javax.inject.Named;
import javax.inject.Singleton;

import java.util.Locale;

import fr.bdf.domain.more.MoreTypesDemo;
import fr.bdf.printer.DiscoverablePrinter;

/**
 * {@link org.springframework.format.Printer} for {@link MoreTypesDemo} 
 *
 * @see org.springframework.format.Printer
 * @see DiscoverablePrinter
 * @see TypeAwarePrinter
 */
@Named
@Singleton
public class MoreTypesDemoPrinter extends DiscoverablePrinter<MoreTypesDemo> {
    public MoreTypesDemoPrinter() {
        super(MoreTypesDemo.class);
    }

    @Override
    public String print(MoreTypesDemo moreTypesDemo, Locale locale) {
        if (moreTypesDemo == null) {
            return "";
        }
        StringBuilder ret = new StringBuilder(256);
        if (moreTypesDemo.getNumberInt() != null) {
            if (ret.length() != 0) {
                ret.append('/');
            }
            ret.append(moreTypesDemo.getNumberInt());
        }
        if (moreTypesDemo.getNumberLong() != null) {
            if (ret.length() != 0) {
                ret.append('/');
            }
            ret.append(moreTypesDemo.getNumberLong());
        }
        if (moreTypesDemo.getNumberDouble() != null) {
            if (ret.length() != 0) {
                ret.append('/');
            }
            ret.append(moreTypesDemo.getNumberDouble());
        }
        return ret.toString();
    }
}
