package com.jaxio.web.selenium.page;

import org.openqa.selenium.WebElement;
import org.openqa.selenium.support.FindBy;

public abstract class AbstractEditPage extends HomePage {
    @FindBy(id = "form:messages")
    public WebElement messages;

    // buttons
    @FindBy(id = "form:quit")
    public WebElement quitButton;
    @FindBy(id = "form:print")
    public WebElement printButton;
    @FindBy(id = "form:delete")
    public WebElement deleteButton;
    @FindBy(name = "form:saveAndClose")
    public WebElement saveButton;
    @FindBy(name = "form:close")
    public WebElement closeButton;
    @FindBy(name = "form:ok")
    public WebElement okButton;

    public void delete() {
        webClient.click(quitButton);
    }

    public void save() {
        webClient.click(saveButton);
    }

    public void close() {
        webClient.click(closeButton);
    }

    public void print() {
        webClient.click(printButton);
    }

    public void ok() {
        webClient.click(okButton);
    }
}