package com.jaxio.web.selenium.support;

import static org.apache.commons.lang.StringUtils.containsIgnoreCase;

import org.openqa.selenium.WebDriver;
import org.openqa.selenium.WebElement;

import com.google.common.base.Function;

public class TextContains implements Function<WebDriver, Boolean> {
    private final WebElement webElement;
    private final String expected;

    public TextContains(WebElement webElement, String expected) {
        this.webElement = webElement;
        this.expected = expected;
    }

    @Override
    public synchronized Boolean apply(WebDriver driver) {
        return containsIgnoreCase(webElement.getText(), expected.trim());
    }
}