package com.jaxio.web.domain;

import java.util.List;
import java.util.Map;
import javax.inject.Inject;

import org.primefaces.model.SortOrder;

import com.jaxio.dao.support.SearchParameters;
import com.jaxio.domain.Role;
import com.jaxio.repository.RoleRepository;
import com.jaxio.web.domain.support.GenericLazyDataModel;

/**
 * Provides server-side pagination for search.
 *
 * @see http://jira.springframework.org/browse/SWF-1224 to avoid instanciate it as a var from your flow 
 */
public class RoleLazyDataModel extends GenericLazyDataModel<Role> {
    private static final long serialVersionUID = 1L;

    @Inject
    transient private RoleRepository roleRepository;
    @Inject
    transient private RoleSearchForm roleSearchForm;

    /**
     * Prepare the search parameters and call the roleRepository finder.
     * Automatically called by PrimeFaces component.
     */
    @Override
    public List<Role> load(int first, int pageSize, String sortField, SortOrder sortOrder, Map<String, String> filters) {
        SearchParameters sp = roleSearchForm.getSearchParameters();
        Role role = roleSearchForm.getRole();
        setRowCount(roleRepository.findCount(role, sp)); // total count so the paginator may display the total number of pages
        populateSearchParameters(sp, first, pageSize, sortField, sortOrder, filters); // load one page of data
        return roleRepository.find(role, sp);
    }
}