package com.jaxio.web.selenium.support.elements;

import java.util.concurrent.TimeUnit;

import org.openqa.selenium.By;

public class SearchForm extends CustomElement {
    public StringInput fullText = new StringInput("form:fullTextQuery");

    public void search() {
        webClient.click(By.id("form:search"));
        webClient.waitUntilEnabled(By.id("form:ajaxSearchSuccess"));
    }

    public void reset() {
        webClient.click(By.id("form:resetSearch"));
        try {
            TimeUnit.MILLISECONDS.sleep(400);
        } catch (InterruptedException e) {
            // until we have feedback like a message
            e.printStackTrace();
        }
    }

    public void excel() {
        // webClient.click(By.id("form:excel"));
    }
}
